import React, { Component } from 'react'
import socketIOClient from 'socket.io-client'
import {
  CartesianGrid,
  XAxis,
  YAxis,
  Line,
  LineChart,
} from 'recharts'
import moment from 'moment'

class App extends Component {
  constructor() {
    super()
    this.state = {
      message: [],
      endpoint: "104.197.98.78" // เชื่อมต่อไปยัง url ของ realtime server
    }
  }

  componentDidMount = () => {
    const { endpoint, message } = this.state
    const temp = message
    const socket = socketIOClient(endpoint)
    socket.on('new-message', (messageNew) => {
      temp.push(messageNew)
      this.setState({ message: temp })
    })
  }

  render() {
    const { message } = this.state
    const data = []
    message.forEach(m => {
      // if (m && m.toLowerCase().includes('#')) {
      //   const hashtags = m.split('#')
      //   if (!hashtags[1].includes('BNK48'))
      //     return
      //   const name = hashtags[1].split('BNK48')[0]
      //   if (!name)
      //     return
      //   const findData = data.find(d => d.name === name)
      //   if (findData) {
      //     findData.count++
      //   } else {
      //     data.push({ name, count: 1 })
      //   }
      // }

      const hashtags = m.created_at.split(' ')
      const name = hashtags[3]
      // data.push({ name: name, count: 1 })
      // var res = name.substring(0, 4);
      const res = moment(m.created_at).format('DD-MM-YYYY HH:mm')
      let found = false
      for (let i = 0; i < data.length; i++) {
        if (moment(data[i].name).format('DD-MM-YYYY HH:mm')  === res) {
          data[i].count++
          found = true
          break
        }
      }
      if (!found) {
        data.push({ name: name, count: 1 })
      }
    })

    return (
      <div>
        <div>
          <LineChart width={500} height={300} data={data}>
            <XAxis dataKey="name" />
            <YAxis />
            <CartesianGrid stroke="#eee" strokeDasharray="5 5" />
            <Line type="monotone" dataKey="count" stroke="#8884d8" />
          </LineChart>
        </div>
        <div style={{ height: '500px', overflow: 'scroll' }}>
          {
            message.map((data, i) =>
              <div key={i} style={{ marginTop: 20, paddingLeft: 50 }} >
                {i + 1} : {data.text}
              </div>
            )
          }
        </div>
      </div>
    )
  }
}

export default App
